# Cell
import os
from dataclasses import dataclass
from typing import Optional, Tuple

import pandas as pd

from .utils import download_file, Info, TimeSeriesDataclass

# Cell
@dataclass
class Yearly:
    seasonality: int = 1
    horizon: int = 6
    freq: str = 'Y'
    sheet_name: str = 'M3Year'
    name: str = 'Yearly'
    n_ts: int = 645

